(()=>{"use strict";this.OMDb=class{constructor(e={}){if(!e.apikey)throw new Error("OMDb API Key is required");this.config={apikey:e.apikey,url:e.url||"https://www.omdbapi.com",debug:e.debug||!1},this._headers={"User-Agent":"Mozilla/5.0","Content-Type":"application/json;charset=utf-8"}}get(e={}){const r=e.id||"",s=e.title||"",t=e.type||null,o=e.year||null,i=e.plot||"short";return new Promise((e,n)=>{const a=r?`${this.config.url}/?apikey=${this.config.apikey}&i=${r}&type=${t}&y=${o}&plot=${i}`:`${this.config.url}/?apikey=${this.config.apikey}&t=${s}&type=${t}&y=${o}&plot=${i}`;GM.xmlHttpRequest({method:"GET",url:a,headers:this._headers,onload:t=>{!0===this.config.debug&&console.log(t);const o=JSON.parse(t.responseText);""===r&&""===s||4!==t.readyState||"[]"===t.responseText||"False"===o.Response?""===r&&""===s?n(new Error("A search query (IMDb ID or Title) is required.")):"False"===o.Response?n(new Error(o.Error)):n(new Error(t)):e(o)}})})}search(e={}){const r=e.search||"",s=e.type||null,t=e.year||null,o=e.page||"1";return new Promise((e,i)=>{GM.xmlHttpRequest({method:"GET",url:`${this.config.url}/?apikey=${this.config.apikey}&s=${r}&type=${s}&y=${t}&page=${o}`,headers:this._headers,onload:s=>{!0===this.config.debug&&console.log(s);const t=JSON.parse(s.responseText);""!==r&&4===s.readyState&&"[]"!==s.responseText&&"False"!==t.Response?e(t.Search):""===r?i(new Error("A search query is required.")):"False"===t.Response?i(new Error(t.Error)):i(new Error(s))}})})}}})();