(()=>{"use strict";this.Trakt=class{constructor(e={}){if(!e.clientID)throw Error("Trakt Client ID is required");this._config={clientID:e.clientID,url:e.url||"https://api.trakt.tv",debug:e.debug||!1},this._headers={"User-Agent":"Mozilla/5.0","Content-Type":"application/json;charset=utf-8","trakt-api-key":this._config.clientID,"trakt-api-version":2},this._debug=(e=>{(this._config.debug||200!==e.status)&&console.log(`${e.status}: ${e.finalUrl}`)})}episodeSummary(e,s,t){return new Promise((r,o)=>{GM.xmlHttpRequest({method:"GET",url:`${this._config.url}/shows/${e}/seasons/${s}/episodes/${t}?extended=full`,headers:this._headers,onload:e=>{this._debug(e),4===e.readyState&&"[]"!==e.responseText?r(JSON.parse(e.responseText)):o(e)}})})}searchID(e,s,t){return new Promise((r,o)=>{GM.xmlHttpRequest({method:"GET",url:`${this._config.url}/search/${e}/${s}?type=${t}`,headers:this._headers,onload:e=>{this._debug(e),4===e.readyState&&"[]"!==e.responseText?r(JSON.parse(e.responseText)):o(e)}})})}seasonsSeason(e,s){return new Promise((t,r)=>{GM.xmlHttpRequest({method:"GET",url:`${this._config.url}/shows/${e}/seasons/${s}`,headers:this._headers,onload:e=>{this._debug(e),4===e.readyState&&"[]"!==e.responseText?t(JSON.parse(e.responseText)):r(e)}})})}seasonSummary(e){return new Promise((s,t)=>{GM.xmlHttpRequest({method:"GET",url:`${this._config.url}/shows/${e}/seasons?extended=full`,headers:this._headers,onload:e=>{this._debug(e),4===e.readyState&&"[]"!==e.responseText?s(JSON.parse(e.responseText)):t(e)}})})}showSummary(e){return new Promise((s,t)=>{GM.xmlHttpRequest({method:"GET",url:`${this._config.url}/shows/${e}?extended=full`,headers:this._headers,onload:e=>{this._debug(e),4===e.readyState&&"[]"!==e.responseText?s(JSON.parse(e.responseText)):t(e)}})})}}})();