(()=>{"use strict";this.Trakt=class{constructor(e={}){if(!e.apikey)throw Error("Trakt API Key is required");this.config={apikey:e.apikey,language:e.language||"en",url:e.url||"https://api.trakt.tv",debug:e.debug||!1},this._headers={"User-Agent":"Mozilla/5.0","Content-Type":"application/json;charset=utf-8","trakt-api-key":this.config.apikey,"trakt-api-version":2}}searchID(e,t,r){return new Promise((s,a)=>{GM.xmlHttpRequest({method:"GET",url:`${this.config.url}/search/${e}/${t}?type=${r}`,headers:this._headers,onload:e=>{if(4===e.readyState&&"[]"!==e.responseText){!0===this.config.debug&&console.log(e);const t=JSON.parse(e.responseText);s(t[0])}else a(e)}})})}}})();