this.Jikan=class{constructor(e={}){this._config={url:e.url||"https://api.jikan.moe",debug:e.debug||!1},this._headers={"User-Agent":"Mozilla/5.0","Content-Type":"application/json;charset=utf-8"},this._debug=e=>{(this._config.debug||200!==e.status)&&console.log(`${e.status}: ${e.finalUrl}`)}}search(e={}){if(!e.query)throw new Error("A search query is required.");const t={query:e.query,type:"series"===e.type?"tv":e.type||void 0,limit:e.limit||25,page:e.page||1};return new Promise(((e,r)=>{GM.xmlHttpRequest({method:"GET",url:`${this._config.url}/v4/anime?q=${encodeURIComponent(t.query)}&type=${t.type}&order_by=popularity&sfw=true&limit=${t.limit}&page=${t.page}`,headers:this._headers,timeout:15e3,onload:t=>{this._debug(t);const s=JSON.parse(t.responseText).data;s.length>0&&4===t.readyState&&"[]"!==t.responseText?e(s):r(new Error("No results"))},onerror:()=>{r(new Error("An error occurs while processing the request"))},ontimeout:()=>{r(new Error("Request times out"))}})}))}};
